package com.stone.core.util;import lombok.Data;import java.io.Serializable;import java.util.Collections;import java.util.List;/** * 分页对象 *  * @title * @date 2019年11月12日 * @version 1.0 * @author stone */@Datapublic class MyPage<T> implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = -8235976291920850306L;	/**	 * 当前页码	 */	private long pageNo;	/**	 * 每一页的记录条数	 */	private long pageSize;	/**	 * 总记录	 */	private long pageDataCount;	/**	 * 总页数	 */	private long pageCount;		/**     * 查询数据列表     */    private List<T> records = Collections.emptyList();		public MyPage(long pageNo, long pageSize) {		this.pageNo = pageNo;		this.pageSize = pageSize;	}	public MyPage(long pageNo, long pageSize, long pageDataCount, long pageCount,List<T> records) {		this.pageNo = pageNo;		this.pageSize = pageSize;		this.pageDataCount = pageDataCount;		this.pageCount = pageCount;		this.records = records;	}	/**	 * 构建分页对象	 * 	 * @param pageNo	 * @param pageSize	 * @param pageDataCount	 * @return	 */	public void build(long pageNo, long pageSize, long pageDataCount) {		long pageCount = pageDataCount % pageSize > 0 ? pageDataCount / pageSize + 1 : pageDataCount / pageSize;		this.setPageNo(pageNo);		this.setPageSize(pageSize);		this.setPageDataCount(pageDataCount);		this.setPageCount(pageCount);	}}